import { Injectable } from '@angular/core';
import { ActivatedRouteSnapshot, CanActivate, Router, RouterStateSnapshot, UrlTree } from '@angular/router';
import { JwtHelperService } from '@auth0/angular-jwt';
import { ToastrService } from 'ngx-toastr';
import { Observable } from 'rxjs';
import { AuthService } from '../services/auth.service';

@Injectable({
  providedIn: 'root'
})
export class LoginGuard implements CanActivate {

  constructor(private authService:AuthService, private toastrService:ToastrService, private router:Router, private jwtHelperService:JwtHelperService) {

  }

  canActivate(
    route: ActivatedRouteSnapshot,
    state: RouterStateSnapshot): Observable<boolean | UrlTree> | Promise<boolean | UrlTree> | boolean | UrlTree {

      let accessToken = localStorage.getItem("accessToken");
      let refreshToken = localStorage.getItem("refreshToken");

      if(accessToken == null || refreshToken == null){
        this.router.navigate(["login"]);
        this.toastrService.info("You should log in.");
        return false;
      }

      var refreshTokenExpirationDate = this.jwtHelperService.getTokenExpirationDate(refreshToken)

      if(this.jwtHelperService.isTokenExpired(accessToken) && this.jwtHelperService.isTokenExpired(refreshToken)){ 
        this.router.navigate(["login"]);
        this.toastrService.info("You should log in.");
        return false;
      }else if(refreshTokenExpirationDate != undefined  && refreshTokenExpirationDate?.getMinutes() >= (new Date(Date.now())).getMinutes() * 95 / 100){
        this.authService.refreshToken(refreshToken).subscribe(res => {
          localStorage.setItem("accessToken", res.accessToken);
          localStorage.setItem("refreshToken", res.refreshToken);
        })
      }else if (this.jwtHelperService.isTokenExpired(accessToken) && !this.jwtHelperService.isTokenExpired(refreshToken)){ 
        this.authService.refreshToken(refreshToken).subscribe(res => {
          localStorage.setItem("accessToken", res.accessToken);
          localStorage.setItem("refreshToken", res.refreshToken);
        })
      } 
    }

}
