import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor,
  HttpErrorResponse
} from '@angular/common/http';
import { catchError, Observable, throwError } from 'rxjs';
import { AuthService } from '../services/auth.service';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';
import { JwtHelperService } from '@auth0/angular-jwt';

@Injectable()
export class HttpRequestInterceptor implements HttpInterceptor {

  toSecond(time : number):number{
    return time / 1000;
  }

  constructor(private authService: AuthService, private toastrService: ToastrService, private router: Router, private jwtHelperService: JwtHelperService) { }

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    let accessToken = localStorage.getItem("accessToken");
    let refreshToken = localStorage.getItem("refreshToken");

    if (refreshToken != null && accessToken != undefined && (this.toSecond(this.jwtHelperService.getTokenExpirationDate(accessToken)!.getTime()) - this.toSecond(new Date(Date.now()).getTime())) === 30) { // when access token is about to expire in 30 seconds.
      this.authService.refreshToken(refreshToken).subscribe(res => {
        localStorage.setItem("accessToken", res.accessToken);
        localStorage.setItem("refreshToken", res.refreshToken);
        return next.handle(request);
      })
    }

    return next.handle(request).pipe(
      catchError((error: HttpErrorResponse) => {
        if (refreshToken != null && error.status === 401) {
          this.authService.refreshToken(refreshToken).subscribe(res => {
            localStorage.setItem("accessToken", res.accessToken);
            localStorage.setItem("refreshToken", res.refreshToken);
            return next.handle(request);
          })
        } else if (error.status === 403) {
          if (accessToken) localStorage.removeItem("accessToken");
          if (refreshToken) localStorage.removeItem("refreshToken");
          this.router.navigate(["login"]);
          this.toastrService.info("You should log in.");
          return next.handle(request);
        }
        return throwError(() => error);
      })
    );
  }
}
