import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { AccessTokenModel } from '../models/accessTokenModel';
import { UserLoginDto } from '../models/DTOs/UserDtos/UserLoginDto';
import { SingleResponseModel } from '../models/sigleResponseModel';

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  apiUrl = "https://localhost:7126/api/auth/";


  constructor(private httpClient:HttpClient) { }

  login(user:UserLoginDto) : Observable<SingleResponseModel<AccessTokenModel>>{
    let newUrl = this.apiUrl + "login";
    return this.httpClient.post<SingleResponseModel<AccessTokenModel>>(newUrl, user);
  }

  isAuthenticated(){
    if(localStorage.getItem("token")){
      return true;
    }
    else{
      return false;
    }
  }
}
