import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Patient } from 'src/app/models/patient';
import { PatientService } from 'src/app/services/patient.service';

@Component({
  selector: 'app-patient',
  templateUrl: './patient.component.html',
  styleUrls: ['./patient.component.scss']
})
export class PatientComponent implements OnInit{

  patients:Patient[] = [];
  dataLoaded = false;

  constructor(private patientService:PatientService, private activatedRoute:ActivatedRoute) {}

  ngOnInit(): void {
    this.activatedRoute.params.subscribe(params =>{
      if(params["hospitalId"]){
        this.getPatientsByHospitalId(params["hospitalId"]);
      }else{
        this.getPatients();
      }
    })
  }

  getPatients(){
    this.patientService.getPatients().subscribe(res => {
      this.patients = res.data;
      this.dataLoaded = true;
    });
  }

  getPatientsByHospitalId(hospitalId:number){
    this.patientService.getPatientsByHospitalId(hospitalId).subscribe(res => {
      this.patients = res.data;
      this.dataLoaded = true;
    })
  }
}
