import { Injectable } from '@angular/core';
import {
  HttpRequest,
  HttpHandler,
  HttpEvent,
  HttpInterceptor,
  HttpErrorResponse
} from '@angular/common/http';
import { catchError, Observable, switchMap, throwError } from 'rxjs';
import { AuthService } from '../services/auth.service';
import { ToastrService } from 'ngx-toastr';
import { Router } from '@angular/router';
import { AuthenticationResponse } from '../models/authenticationResponse';

@Injectable()
export class HttpRequestInterceptor implements HttpInterceptor {

  constructor(private authService: AuthService, private toastrService: ToastrService, private router: Router) { }

  intercept(request: HttpRequest<unknown>, next: HttpHandler): Observable<HttpEvent<unknown>> {
    let accessToken = localStorage.getItem("accessToken");
    let refreshToken = localStorage.getItem("refreshToken");

    return next.handle(request).pipe(
      catchError((error: HttpErrorResponse) => {
        if (error.status === 403) {
          if (accessToken) localStorage.removeItem("accessToken");
          if (refreshToken) localStorage.removeItem("refreshToken");
          this.router.navigate(["login"]);
          this.toastrService.info("You should log in.");
          return next.handle(request);
        }
        else if (refreshToken != null && error.status === 401) {
          return this.authService.refreshToken(refreshToken)
          .pipe(
            switchMap((res:AuthenticationResponse) => {
              console.log(res)
              localStorage.setItem("accessToken", res.accessToken);
              localStorage.setItem("refreshToken", res.refreshToken);
              return next.handle(this.reSendRequest(request));
            })
          )
        } 
        else
          return next.handle(this.reSendRequest(request));
      })
    );
  }
  reSendRequest = (request: HttpRequest<any>): HttpRequest<any> => {
		if(localStorage.getItem("accessToken")){
      request = request.clone({
        headers: request.headers.set("Authorization","Bearer " + localStorage.getItem("accessToken"))
      })
    }
    return request;
	};

}
